COLORMAP = [
    (255, 0, 255),
    (0, 255, 0),
    (0, 128, 255),
    (255, 255, 0),
    (0, 255, 255),
    (255, 128, 255),
    (128, 255, 0),
    (255, 0, 128),
    (128, 255, 255),
    (255, 255, 255),
    (255, 255, 128),
    (255, 128, 0),
    (128, 0, 255),
    (0, 0, 255),
    (0, 255, 128),
    (255, 0, 0)
]


def idx2color(idx):
    return COLORMAP[idx % len(COLORMAP)]
